    {"framework": "ISO_27001", "reference": "A.5.16", "title": "Identity management", "description": "The full life cycle of identities shall be managed.", "category": "Organizational Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 16},
    {"framework": "ISO_27001", "reference": "A.5.17", "title": "Authentication information", "description": "Allocation and management of authentication information shall be controlled by a management process including advising personnel on the appropriate handling of authentication information.", "category": "Organizational Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 17},
    {"framework": "ISO_27001", "reference": "A.5.18", "title": "Access rights", "description": "Access rights to information and other associated assets shall be provisioned, reviewed, modified and removed in accordance with the organization's topic-specific policy on and rules for access control.", "category": "Organizational Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 18},
    {"framework": "ISO_27001", "reference": "A.5.19", "title": "Information security in supplier relationships", "description": "Processes and procedures shall be defined and implemented to manage the information security risks associated with the use of supplier's products or services.", "category": "Organizational Controls", "subcategory": "Supplier Relationships", "suggested_domain": "Third-Party Management", "suggested_control_type": "PREVENTIVE", "sort_order": 19},
    {"framework": "ISO_27001", "reference": "A.5.20", "title": "Addressing information security within supplier agreements", "description": "Relevant information security requirements shall be established and agreed with each supplier based on the type of supplier relationship.", "category": "Organizational Controls", "subcategory": "Supplier Relationships", "suggested_domain": "Third-Party Management", "suggested_control_type": "PREVENTIVE", "sort_order": 20},
    {"framework": "ISO_27001", "reference": "A.5.21", "title": "Managing information security in the ICT supply chain", "description": "Processes and procedures shall be defined and implemented to manage the information security risks associated with the ICT products and services supply chain.", "category": "Organizational Controls", "subcategory": "Supplier Relationships", "suggested_domain": "Third-Party Management", "suggested_control_type": "PREVENTIVE", "sort_order": 21},
    {"framework": "ISO_27001", "reference": "A.5.22", "title": "Monitoring, review and change management of supplier services", "description": "The organization shall regularly monitor, review, evaluate and manage change in supplier information security practices and service delivery.", "category": "Organizational Controls", "subcategory": "Supplier Relationships", "suggested_domain": "Third-Party Management", "suggested_control_type": "DETECTIVE", "sort_order": 22},
    {"framework": "ISO_27001", "reference": "A.5.23", "title": "Information security for use of cloud services", "description": "Processes for acquisition, use, management and exit from cloud services shall be established in accordance with the organization's information security requirements.", "category": "Organizational Controls", "subcategory": "Cloud Services", "suggested_domain": "Third-Party Management", "suggested_control_type": "PREVENTIVE", "sort_order": 23},
    {"framework": "ISO_27001", "reference": "A.5.24", "title": "Information security incident management planning and preparation", "description": "The organization shall plan and prepare for managing information security incidents by defining, establishing and communicating information security incident management processes, roles and responsibilities.", "category": "Organizational Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "PREVENTIVE", "sort_order": 24},
    {"framework": "ISO_27001", "reference": "A.5.25", "title": "Assessment and decision on information security events", "description": "The organization shall assess information security events and decide if they are to be categorized as information security incidents.", "category": "Organizational Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "DETECTIVE", "sort_order": 25},
    {"framework": "ISO_27001", "reference": "A.5.26", "title": "Response to information security incidents", "description": "Information security incidents shall be responded to in accordance with the documented procedures.", "category": "Organizational Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "CORRECTIVE", "sort_order": 26},
    {"framework": "ISO_27001", "reference": "A.5.27", "title": "Learning from information security incidents", "description": "Knowledge gained from information security incidents shall be used to strengthen and improve the information security controls.", "category": "Organizational Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "CORRECTIVE", "sort_order": 27},
    {"framework": "ISO_27001", "reference": "A.5.28", "title": "Collection of evidence", "description": "The organization shall establish and implement procedures for the identification, collection, acquisition and preservation of evidence related to information security events.", "category": "Organizational Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "DETECTIVE", "sort_order": 28},
    {"framework": "ISO_27001", "reference": "A.5.29", "title": "Information security during disruption", "description": "The organization shall plan how to maintain information security at an appropriate level during disruption.", "category": "Organizational Controls", "subcategory": "Business Continuity", "suggested_domain": "Business Continuity", "suggested_control_type": "PREVENTIVE", "sort_order": 29},
    {"framework": "ISO_27001", "reference": "A.5.30", "title": "ICT readiness for business continuity", "description": "ICT readiness shall be planned, implemented, maintained and tested based on business continuity objectives and ICT continuity requirements.", "category": "Organizational Controls", "subcategory": "Business Continuity", "suggested_domain": "Business Continuity", "suggested_control_type": "PREVENTIVE", "sort_order": 30},
    {"framework": "ISO_27001", "reference": "A.5.31", "title": "Legal, statutory, regulatory and contractual requirements", "description": "Legal, statutory, regulatory and contractual requirements relevant to information security and the organization's approach to meet these requirements shall be identified, documented and kept up to date.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Governance", "suggested_control_type": "PREVENTIVE", "sort_order": 31},
    {"framework": "ISO_27001", "reference": "A.5.32", "title": "Intellectual property rights", "description": "The organization shall implement appropriate procedures to protect intellectual property rights.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Governance", "suggested_control_type": "PREVENTIVE", "sort_order": 32},
    {"framework": "ISO_27001", "reference": "A.5.33", "title": "Protection of records", "description": "Records shall be protected from loss, destruction, falsification, unauthorized access and unauthorized release.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 33},
    {"framework": "ISO_27001", "reference": "A.5.34", "title": "Privacy and protection of personal identifiable information (PII)", "description": "The organization shall identify and meet the requirements regarding the preservation of privacy and protection of PII as applicable according to relevant laws, regulations and contractual requirements.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 34},
    {"framework": "ISO_27001", "reference": "A.5.35", "title": "Independent review of information security", "description": "The organization's approach to managing information security and its implementation including people, processes and technologies shall be reviewed independently at planned intervals or when significant changes occur.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Governance", "suggested_control_type": "DETECTIVE", "sort_order": 35},
    {"framework": "ISO_27001", "reference": "A.5.36", "title": "Compliance with policies, rules and standards for information security", "description": "Compliance with the organization's information security policy, topic-specific policies, rules and standards shall be regularly reviewed.", "category": "Organizational Controls", "subcategory": "Compliance", "suggested_domain": "Governance", "suggested_control_type": "DETECTIVE", "sort_order": 36},
    {"framework": "ISO_27001", "reference": "A.5.37", "title": "Documented operating procedures", "description": "Operating procedures for information processing facilities shall be documented and made available to personnel who need them.", "category": "Organizational Controls", "subcategory": "Operations Security", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 37},

    # ============================================================
    # A.6 – People Controls (8 controls)
    # ============================================================
    {"framework": "ISO_27001", "reference": "A.6.1", "title": "Screening", "description": "Background verification checks on all candidates to become personnel shall be carried out prior to joining the organization and on an ongoing basis taking into account applicable laws, regulations and ethics and be proportional to the business requirements, the classification of the information to be accessed and the perceived risks.", "category": "People Controls", "subcategory": "Prior to Employment", "suggested_domain": "Human Resources", "suggested_control_type": "PREVENTIVE", "sort_order": 38},
    {"framework": "ISO_27001", "reference": "A.6.2", "title": "Terms and conditions of employment", "description": "The employment contractual agreements shall state the personnel's and the organization's responsibilities for information security.", "category": "People Controls", "subcategory": "Prior to Employment", "suggested_domain": "Human Resources", "suggested_control_type": "PREVENTIVE", "sort_order": 39},
    {"framework": "ISO_27001", "reference": "A.6.3", "title": "Information security awareness, education and training", "description": "Personnel of the organization and relevant interested parties shall receive appropriate information security awareness, education and training and regular updates of the organization's information security policy, topic-specific policies and procedures, as relevant for their job function.", "category": "People Controls", "subcategory": "During Employment", "suggested_domain": "Training & Awareness", "suggested_control_type": "PREVENTIVE", "sort_order": 40},
    {"framework": "ISO_27001", "reference": "A.6.4", "title": "Disciplinary process", "description": "A disciplinary process shall be formalized and communicated to take actions against personnel and other relevant interested parties who have committed an information security policy violation.", "category": "People Controls", "subcategory": "During Employment", "suggested_domain": "Human Resources", "suggested_control_type": "CORRECTIVE", "sort_order": 41},
    {"framework": "ISO_27001", "reference": "A.6.5", "title": "Responsibilities after termination or change of employment", "description": "Information security responsibilities and duties that remain valid after termination or change of employment shall be defined, enforced and communicated to relevant personnel and other interested parties.", "category": "People Controls", "subcategory": "Termination and Change of Employment", "suggested_domain": "Human Resources", "suggested_control_type": "PREVENTIVE", "sort_order": 42},
    {"framework": "ISO_27001", "reference": "A.6.6", "title": "Confidentiality or non-disclosure agreements", "description": "Confidentiality or non-disclosure agreements reflecting the organization's needs for the protection of information shall be identified, documented, regularly reviewed and signed by personnel and other relevant interested parties.", "category": "People Controls", "subcategory": "During Employment", "suggested_domain": "Human Resources", "suggested_control_type": "PREVENTIVE", "sort_order": 43},
    {"framework": "ISO_27001", "reference": "A.6.7", "title": "Remote working", "description": "Security measures shall be implemented when personnel are working remotely to protect information accessed, processed or stored outside the organization's premises.", "category": "People Controls", "subcategory": "During Employment", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 44},
    {"framework": "ISO_27001", "reference": "A.6.8", "title": "Information security event reporting", "description": "The organization shall provide a mechanism for personnel to report observed or suspected information security events through appropriate channels in a timely manner.", "category": "People Controls", "subcategory": "Incident Management", "suggested_domain": "Incident Management", "suggested_control_type": "DETECTIVE", "sort_order": 45},

    # ============================================================
    # A.7 – Physical Controls (14 controls)
    # ============================================================
    {"framework": "ISO_27001", "reference": "A.7.1", "title": "Physical security perimeters", "description": "Security perimeters shall be defined and used to protect areas that contain information and other associated assets.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 46},
    {"framework": "ISO_27001", "reference": "A.7.2", "title": "Physical entry", "description": "Secure areas shall be protected by appropriate entry controls and access points.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 47},
    {"framework": "ISO_27001", "reference": "A.7.3", "title": "Securing offices, rooms and facilities", "description": "Physical security for offices, rooms and facilities shall be designed and implemented.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 48},
    {"framework": "ISO_27001", "reference": "A.7.4", "title": "Physical security monitoring", "description": "Premises shall be continuously monitored for unauthorized physical access.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "DETECTIVE", "sort_order": 49},
    {"framework": "ISO_27001", "reference": "A.7.5", "title": "Protecting against physical and environmental threats", "description": "Protection against physical and environmental threats, such as natural disasters and other intentional or unintentional physical threats to infrastructure shall be designed and implemented.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 50},
    {"framework": "ISO_27001", "reference": "A.7.6", "title": "Working in secure areas", "description": "Security measures for working in secure areas shall be designed and implemented.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 51},
    {"framework": "ISO_27001", "reference": "A.7.7", "title": "Clear desk and clear screen", "description": "Clear desk rules for papers and removable storage media and clear screen rules for information processing facilities shall be defined and appropriately enforced.", "category": "Physical Controls", "subcategory": "Secure Areas", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 52},
    {"framework": "ISO_27001", "reference": "A.7.8", "title": "Equipment siting and protection", "description": "Equipment shall be sited securely and protected.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 53},
    {"framework": "ISO_27001", "reference": "A.7.9", "title": "Security of assets off-premises", "description": "Off-site assets shall be protected.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Asset Management", "suggested_control_type": "PREVENTIVE", "sort_order": 54},
    {"framework": "ISO_27001", "reference": "A.7.10", "title": "Storage media", "description": "Storage media shall be managed through their life cycle of acquisition, use, transportation and disposal in accordance with the organization's classification scheme and handling requirements.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Asset Management", "suggested_control_type": "PREVENTIVE", "sort_order": 55},
    {"framework": "ISO_27001", "reference": "A.7.11", "title": "Supporting utilities", "description": "Information processing facilities shall be protected from power failures and other disruptions caused by failures in supporting utilities.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 56},
    {"framework": "ISO_27001", "reference": "A.7.12", "title": "Cabling security", "description": "Cables carrying power, data or supporting information services shall be protected from interception, interference or damage.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 57},
    {"framework": "ISO_27001", "reference": "A.7.13", "title": "Equipment maintenance", "description": "Equipment shall be maintained correctly to ensure availability, integrity and confidentiality of information.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Physical Security", "suggested_control_type": "PREVENTIVE", "sort_order": 58},
    {"framework": "ISO_27001", "reference": "A.7.14", "title": "Secure disposal or re-use of equipment", "description": "Items of equipment containing storage media shall be verified to ensure that any sensitive data and licensed software has been removed or securely overwritten prior to disposal or re-use.", "category": "Physical Controls", "subcategory": "Equipment", "suggested_domain": "Asset Management", "suggested_control_type": "PREVENTIVE", "sort_order": 59},

    # ============================================================
    # A.8 – Technological Controls (34 controls)
    # ============================================================
    {"framework": "ISO_27001", "reference": "A.8.1", "title": "User endpoint devices", "description": "Information stored on, processed by or accessible via user endpoint devices shall be protected.", "category": "Technological Controls", "subcategory": "Endpoint Security", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 60},
    {"framework": "ISO_27001", "reference": "A.8.2", "title": "Privileged access rights", "description": "The allocation and use of privileged access rights shall be restricted and managed.", "category": "Technological Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 61},
    {"framework": "ISO_27001", "reference": "A.8.3", "title": "Information access restriction", "description": "Access to information and other associated assets shall be restricted in accordance with the established topic-specific policy on access control.", "category": "Technological Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 62},
    {"framework": "ISO_27001", "reference": "A.8.4", "title": "Access to source code", "description": "Read and write access to source code, development tools and software libraries shall be appropriately managed.", "category": "Technological Controls", "subcategory": "Access Control", "suggested_domain": "Secure Development", "suggested_control_type": "PREVENTIVE", "sort_order": 63},
    {"framework": "ISO_27001", "reference": "A.8.5", "title": "Secure authentication", "description": "Secure authentication technologies and procedures shall be established and implemented based on information access restrictions and the topic-specific policy on access control.", "category": "Technological Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 64},
    {"framework": "ISO_27001", "reference": "A.8.6", "title": "Capacity management", "description": "The use of resources shall be monitored and adjusted in line with current and expected capacity requirements.", "category": "Technological Controls", "subcategory": "Operations Security", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 65},
    {"framework": "ISO_27001", "reference": "A.8.7", "title": "Protection against malware", "description": "Protection against malware shall be implemented and supported by appropriate user awareness.", "category": "Technological Controls", "subcategory": "Malware Protection", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 66},
    {"framework": "ISO_27001", "reference": "A.8.8", "title": "Management of technical vulnerabilities", "description": "Information about technical vulnerabilities of information systems in use shall be obtained, the organization's exposure to such vulnerabilities shall be evaluated and appropriate measures shall be taken.", "category": "Technological Controls", "subcategory": "Vulnerability Management", "suggested_domain": "Vulnerability Management", "suggested_control_type": "PREVENTIVE", "sort_order": 67},
    {"framework": "ISO_27001", "reference": "A.8.9", "title": "Configuration management", "description": "Configurations, including security configurations, of hardware, software, services and networks shall be established, documented, implemented, monitored and reviewed.", "category": "Technological Controls", "subcategory": "Configuration Management", "suggested_domain": "Change Management", "suggested_control_type": "PREVENTIVE", "sort_order": 68},
    {"framework": "ISO_27001", "reference": "A.8.10", "title": "Information deletion", "description": "Information stored in information systems, devices or in any other storage media shall be deleted when no longer required.", "category": "Technological Controls", "subcategory": "Data Protection", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 69},
    {"framework": "ISO_27001", "reference": "A.8.11", "title": "Data masking", "description": "Data masking shall be used in accordance with the organization's topic-specific policy on access control and other related topic-specific policies, and business requirements, taking applicable legislation into consideration.", "category": "Technological Controls", "subcategory": "Data Protection", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 70},
    {"framework": "ISO_27001", "reference": "A.8.12", "title": "Data leakage prevention", "description": "Data leakage prevention measures shall be applied to systems, networks and any other devices that process, store or transmit sensitive information.", "category": "Technological Controls", "subcategory": "Data Protection", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 71},
    {"framework": "ISO_27001", "reference": "A.8.13", "title": "Information backup", "description": "Backup copies of information, software and systems shall be maintained and regularly tested in accordance with the agreed topic-specific policy on backup.", "category": "Technological Controls", "subcategory": "Backup", "suggested_domain": "Business Continuity", "suggested_control_type": "PREVENTIVE", "sort_order": 72},
    {"framework": "ISO_27001", "reference": "A.8.14", "title": "Redundancy of information processing facilities", "description": "Information processing facilities shall be implemented with redundancy sufficient to meet availability requirements.", "category": "Technological Controls", "subcategory": "Backup", "suggested_domain": "Business Continuity", "suggested_control_type": "PREVENTIVE", "sort_order": 73},
    {"framework": "ISO_27001", "reference": "A.8.15", "title": "Logging", "description": "Logs that record activities, exceptions, faults and other relevant events shall be produced, stored, protected and analysed.", "category": "Technological Controls", "subcategory": "Logging and Monitoring", "suggested_domain": "Security Monitoring", "suggested_control_type": "DETECTIVE", "sort_order": 74},
    {"framework": "ISO_27001", "reference": "A.8.16", "title": "Monitoring activities", "description": "Networks, systems and applications shall be monitored for anomalous behaviour and appropriate actions taken to evaluate potential information security incidents.", "category": "Technological Controls", "subcategory": "Logging and Monitoring", "suggested_domain": "Security Monitoring", "suggested_control_type": "DETECTIVE", "sort_order": 75},
    {"framework": "ISO_27001", "reference": "A.8.17", "title": "Clock synchronization", "description": "The clocks of information processing systems used by the organization shall be synchronized to approved time sources.", "category": "Technological Controls", "subcategory": "Logging and Monitoring", "suggested_domain": "Security Operations", "suggested_control_type": "PREVENTIVE", "sort_order": 76},
    {"framework": "ISO_27001", "reference": "A.8.18", "title": "Use of privileged utility programs", "description": "The use of utility programs that can be capable of overriding system and application controls shall be restricted and tightly controlled.", "category": "Technological Controls", "subcategory": "Access Control", "suggested_domain": "Access Control", "suggested_control_type": "PREVENTIVE", "sort_order": 77},
    {"framework": "ISO_27001", "reference": "A.8.19", "title": "Installation of software on operational systems", "description": "Procedures and measures shall be implemented to securely manage software installation on operational systems.", "category": "Technological Controls", "subcategory": "Operations Security", "suggested_domain": "Change Management", "suggested_control_type": "PREVENTIVE", "sort_order": 78},
    {"framework": "ISO_27001", "reference": "A.8.20", "title": "Networks security", "description": "Networks and network devices shall be secured, managed and controlled to protect information in systems and applications.", "category": "Technological Controls", "subcategory": "Network Security", "suggested_domain": "Network Security", "suggested_control_type": "PREVENTIVE", "sort_order": 79},
    {"framework": "ISO_27001", "reference": "A.8.21", "title": "Security of network services", "description": "Security mechanisms, service levels and service requirements of network services shall be identified, implemented and monitored.", "category": "Technological Controls", "subcategory": "Network Security", "suggested_domain": "Network Security", "suggested_control_type": "PREVENTIVE", "sort_order": 80},
    {"framework": "ISO_27001", "reference": "A.8.22", "title": "Segregation of networks", "description": "Groups of information services, users and information systems shall be segregated in the organization's networks.", "category": "Technological Controls", "subcategory": "Network Security", "suggested_domain": "Network Security", "suggested_control_type": "PREVENTIVE", "sort_order": 81},
    {"framework": "ISO_27001", "reference": "A.8.23", "title": "Web filtering", "description": "Access to external websites shall be managed to reduce exposure to malicious content.", "category": "Technological Controls", "subcategory": "Network Security", "suggested_domain": "Network Security", "suggested_control_type": "PREVENTIVE", "sort_order": 82},
    {"framework": "ISO_27001", "reference": "A.8.24", "title": "Use of cryptography", "description": "Rules for the effective use of cryptography, including cryptographic key management, shall be defined and implemented.", "category": "Technological Controls", "subcategory": "Cryptography", "suggested_domain": "Cryptography", "suggested_control_type": "PREVENTIVE", "sort_order": 83},
    {"framework": "ISO_27001", "reference": "A.8.25", "title": "Secure development life cycle", "description": "Rules for the secure development of software and systems shall be established and applied.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Secure Development", "suggested_control_type": "PREVENTIVE", "sort_order": 84},
    {"framework": "ISO_27001", "reference": "A.8.26", "title": "Application security requirements", "description": "Information security requirements shall be identified, specified and approved when developing or acquiring applications.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Secure Development", "suggested_control_type": "PREVENTIVE", "sort_order": 85},
    {"framework": "ISO_27001", "reference": "A.8.27", "title": "Secure system architecture and engineering principles", "description": "Principles for engineering secure systems shall be established, documented, maintained and applied to any information system development activities.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Secure Development", "suggested_control_type": "PREVENTIVE", "sort_order": 86},
    {"framework": "ISO_27001", "reference": "A.8.28", "title": "Secure coding", "description": "Secure coding principles shall be applied to software development.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Secure Development", "suggested_control_type": "PREVENTIVE", "sort_order": 87},
    {"framework": "ISO_27001", "reference": "A.8.29", "title": "Security testing in development and acceptance", "description": "Security testing processes shall be defined and implemented in the development life cycle.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Secure Development", "suggested_control_type": "DETECTIVE", "sort_order": 88},
    {"framework": "ISO_27001", "reference": "A.8.30", "title": "Outsourced development", "description": "The organization shall direct, monitor and review the activities related to outsourced system development.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Third-Party Management", "suggested_control_type": "DETECTIVE", "sort_order": 89},
    {"framework": "ISO_27001", "reference": "A.8.31", "title": "Separation of development, test and production environments", "description": "Development, testing and production environments shall be separated and secured.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Change Management", "suggested_control_type": "PREVENTIVE", "sort_order": 90},
