    {"framework": "ISO_27001", "reference": "A.8.33", "title": "Test information", "description": "Test information shall be appropriately selected, protected and managed.", "category": "Technological Controls", "subcategory": "Secure Development", "suggested_domain": "Data Protection", "suggested_control_type": "PREVENTIVE", "sort_order": 92},
